


class Solution:
    def longestPalindrome(self, s: str) -> str:
        lon=""
        for i in range(len(s)):
            for j in range(i+1,len(s)+1):
                cs=""
                for x in range(i,j):
                    cs+=s[x]
                if cs==cs[::-1]:
                    if len(lon) < len(cs):
                        lon = cs

        return lon
                
slt = Solution()

